mapper.select:
UPDATE tb_domain SET 
	domain_name = #{domainName } 
WHERE domain_seq = #{domainSeq }

UPDATE tb_lto SET 
	lto_name = #{ltoName } 
WHERE lto_seq = #{ltoSeq }

UPDATE tb_sto SET 
	sto_name = #{stoName } ,
	sto_contents = #{stoContents } ,
	sto_arr_std_cnt = #{stoArrStdCnt } ,
	sto_arr_std_pst = #{stoArrStdPst } 
WHERE sto_seq = #{stoSeq }
