mapper.select:
SELECT * FROM ( SELECT * FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS rownum , tn.notice_seq , tn.notice_title , tn.notice_contents , tn.notice_reg_mbr_seq , (SELECT member_name FROM tb_member tm WHERE tm.member_seq = tn.notice_reg_mbr_seq) AS member_name , tn.notice_reg_dt FROM tb_notice tn , (SELECT @ROWNUM := 0 ) rn WHERE 1=1
AND ( notice_title LIKE CONCAT('%',#{searchText1},'%') OR notice_contents LIKE CONCAT('%',#{searchText1},'%') )
) sub ORDER BY sub.rownum DESC ) main LIMIT #{startNum}, 10
